# bootstrap-config.ps1# Sets up CyberAi for ESM-compatible ts-node executionWrite-Host "?? Bootstrapping CyberAi config..."# Ensure you're in the right directorySet-Location -Path "$PSScriptRoot"# 1. Create or overwrite package.json@'{  "name": "cyberai",  "version": "1.0.0",  "description": "**CyberAi (formerly CuberAi)** - AI-Powered Smart Contract Security & Orchestration Platform",  "type": "module",  "main": "index.js",  "scripts": {    "resolve-conflicts": "ts-node scripts/SmartBrainConflictResolver.mts"  },  "dependencies": {    "openai": "^6.16.0"  },  "devDependencies": {    "ts-node": "^10.9.2",    "typescript": "^5.3.3",    "@types/node": "^25.0.5"  }}'@ | Set-Content -Encoding UTF8 -Path "package.json"# 2. Create tsconfig.json@'{  "compilerOptions": {    "target": "ES2020",    "module": "ESNext",    "moduleResolution": "node",    "esModuleInterop": true,    "resolveJsonModule": true,    "allowSyntheticDefaultImports": true,    "strict": true,    "skipLibCheck": true  },  "ts-node": {    "esm": true  }}'@ | Set-Content -Encoding UTF8 -Path "tsconfig.json"# 2.5 Add CANE config to async.env$caneEnv = @'# CyberAi CANE integrationCANE_ENABLED=trueCANE_ENDPOINT=https://cane.cyberai.network/apiCANE_TOKEN=your-cane-token-here'@$caneEnv | Set-Content -Encoding UTF8 -Path "async.env"Write-Host "üîê Added CANE config to async.env"# 3. Rename script to .mts$oldScript = "scripts\SmartBrainConflictResolver.ts"$newScript = "scripts\SmartBrainConflictResolver.mts"if (Test-Path $oldScript) {  Rename-Item -Path $oldScript -NewName (Split-Path $newScript -Leaf)  Write-Host "?? Renamed $oldScript ? $newScript"}# 4. Install all dependenciesWrite-Host "?? Installing dependencies..."npm installWrite-Host "`n? Setup complete. You can now run:"Write-Host "   $env:OPENAI_API_KEY = 'sk-...'"Write-Host "   npm run resolve-conflicts"